import react, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
  // setting the initial state to an empty input, destructuring so later on change the change stores in the updated State;
  // u can have multiple states in one component, totally seperated from each other;
  // if you read the value of the input element, it will always be a string - e.g. a number as a string - thats why all states are initialised by a string;
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  const titleChangeHandler = (event) => {
    // equal for the change event as 'onKeyDown' we would use 'event.key' - just the way the event object is structured for this specific event listener we r using;
    setEnteredTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.target.value);
  };

  const submitHandler = (event, form) => {
    // so the page doesnt reload on submit;
    event.preventDefault();

    const ExpenseDataObject = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };

    console.log(ExpenseDataObject);
    form.reset(); 
  };

  return (
    <form onSubmit={submitHandler}>
      {/* regular html form so far, regular html min max attributes */}
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            onChange={amountChangeHandler}
          />{" "}
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          {/* Only years expenses can be set to cause we will set the output to those dates;  */}
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            onChange={dateChangeHandler}
          />
        </div>
      </div>

      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
